import { PrinterInfo } from "enum";
/**
 * 打印机查询接口定义
 */
interface IQueryApi {
    /**
     * 获取打印机实时状态
     * @returns {Promise} 返回打印机状态对象
     */
    getStatus: () => Promise<any>;
    /**
     * 获取打印机详细信息
     * @param {PrinterInfo} infoType - 需要查询的信息类型
     * @returns {Promise<any>} 返回打印机信息对象
     */
    getInfo: (infoType: PrinterInfo) => Promise<any>;
}
export default IQueryApi;
